import React, { useState, useEffect } from "react";
import Navbar from "./Navbar.js";

function App() {
  // API: https://reqres.in/api/users?page=1
  const [users, setUsers] = useState([]);
  const getUsers = async () => {
    const response = await fetch("https://reqres.in/api/users?page=1")
    const users = await response.json();
    setUsers(users.data);
  };
  const handleUserClick = (user) => {

    useEffect(() => {
      getUsers();
    }, []);

  };
  return (
    <div className="App">
      <Navbar oper = {handleUserClick} />
      <div className="grid">
        {
          users.map((user) => {
            return (
              <div key={user.id} >
                <div className="users">
                  <div>
                    <img src={user.avatar} />
                  </div>
                  <div className="details">
                    <p className="id">
                      <span>ID: </span>
                      {user.id}
                    </p>
                    <div className="name">
                      <p className="f_name">
                        <span>First Name: </span>
                        {user.first_name}
                      </p>
                      <p className="l_name">
                        <span>Last Name: </span>
                        {user.last_name}
                      </p>
                    </div>
                    <p className="email">
                      <span>E-mail: </span>
                      {user.email}
                    </p>
                  </div>
                </div>
              </div>
            )
          })
        }
      </div>
    </div>
  );
}

export default App;
