import React, { useState, useEffect } from "react";
import Navbar from "./Navbar.js";

function App() {
  const [users, setUsers] = useState([]);

  const fetchUserData = () => {
    fetch("https://reqres.in/api/users?page=1")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setUsers(data)
      })
  }
  useEffect(() => {
    fetchUserData()
  }, [])
