            </div>
            {users.map(user => (
              <li key={user.id}>{user.name}</li>
            ))}
          </div>
        )}
        <div>
          <img src="https://reqres.in/img/faces/2-image.jpg" />
        </div>
        <div className="details">
          <p className="id"><span>ID: </span>2</p>
          <div className="name">
            <p className="f_name"><span>First Name: </span>Janet</p>
            <p className="l_name"><span>Last Name: </span>Weaver</p>
          </div>
          <p className="email">
            <span>E-mail: </span>
            {users}
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
