  const [loading, setLoading] = useState(fa);


    fetch("https://reqres.in/api/users?page=1")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setUsers(data.data[2])
      })

  return (
    <div className="App">
      <Navbar />
      <div className="users">
        <div>
          <img src={users.avatar} />
        </div>
        <div className="details">
          <p className="id"><span>ID: </span>
            {users.id}
          </p>
          <div className="name">
            <p className="f_name"><span>First Name: </span>
              {users.first_name}
            </p>
            <p className="l_name"><span>Last Name: </span>
              {users.last_name}
            </p>
          </div>
          <p className="email">
            <span>E-mail: </span>
            { users.email }
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
